import "./CreatePlanta.css";
import axios from 'axios';
import React, { useEffect, useState,} from 'react';
import {
    Link
  } from "react-router-dom";
    
  function CreatePlanta() {

    const [plantas, setPlantas] = useState([]);
    const [bloqueA, setBloqueA] = useState([]);
    const [bloqueB, setBloqueB] = useState([]);
    const [bloqueC, setBloqueC] = useState([]);

    useEffect(() => {
        (async () => {
            const res = await axios.get(`http://localhost:8090/api/GetPlantas`, {
            });
            setPlantas(res.data);
            const idP = res.data[0].id;
            const res1 = await axios.get(`http://localhost:8090/api/GetBloqueInfo/${idP}/${1}`);
            setBloqueA(res1.data[0].espacioDisponible);
            const res2 = await axios.get(`http://localhost:8090/api/GetBloqueInfo/${idP}/${2}`);
            setBloqueB(res2.data[0].espacioDisponible);
            const res3 = await axios.get(`http://localhost:8090/api/GetBloqueInfo/${idP}/${3}`);
            setBloqueC(res3.data[0].espacioDisponible);
        })()
    },[])

    return (
    <div className="Corporativo">
        <div className="marco">
            <Link to={`/CrearPlanta`}>
                <button>Crear Planta</button>
            </Link>
            <Link to={`/ModificarPlanta`}>
                <button>Modificar Planta</button>
            </Link>
            <Link to={`/EliminarPlanta`}>
                <button>Eliminar Planta</button>
            </Link>
        </div>
        <div className="Tabla">
          <div className="div-c">
              <div className="div-nuevo2">
                  <table id="customers">
                      <thead>
                        <tr>
                            <th>Planta</th>
                            <th>Nombre</th>
                            <th>Provincia</th>
                            <th>Canton</th>
                            <th>Distrito</th>
                            <th>Direccion exacta</th>
                            <th>Telefono</th>
                            <th>Cantidad BloqueA</th>
                            <th>Cantidad BloqueB</th>
                            <th>Cantidad BloqueC</th>
                        </tr>
                      </thead>
                  { 
                      plantas.map(planta => (
                          <tbody>
                              <tr>
                                  <td>{planta.id}</td>
                                  <td>{planta.nombre}</td>
                                  <td>{planta.provincia}</td>
                                  <td>{planta.canton}</td>
                                  <td>{planta.distrito}</td>
                                  <td>{planta.direccion_exacta}</td>
                                  <td>{planta.telefono}</td>
                                  <td>{bloqueA}</td>
                                  <td>{bloqueB}</td>
                                  <td>{bloqueC}</td>
                              </tr>     
                          </tbody>
                      ))
                  }
                  </table>
              </div>
          </div>
        </div>
    </div>
    );
  }
  
  export default CreatePlanta;
