import axios from 'axios';
import React, { useEffect, useState } from 'react';
import Button from "react-bootstrap/Button";
import "./CreatePlanta.css";
import Container from "react-bootstrap/Container";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import Image from "react-bootstrap/Image";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  NavLink,
  Link
} from "react-router-dom";

function CrearP() {

  const [plantas, setPlantas] = useState([]);
    useEffect(() => {
        (async () => {
            const res = await axios.get(`http://localhost:8090/api/GetPlantas`, {
            });
            setPlantas(res.data);
        })()
    },[])

  return (
    <div className="Corporativo">
        <div className="marco">
            <Link to={`/CrearPlanta`}>
                <button>Crear Planta</button>
            </Link>
            <Link to={`/ModificarPlanta`}>
                <button>Modificar Planta</button>
            </Link>
            <Link to={`/EliminarPlanta`}>
                <button>Eliminar Planta</button>
            </Link>
        </div>
        <div className="div-c">
            <div className="div-nuevo2">
                <table id="customers">
                { 
                    plantas.map(planta => (
                        <thead>
                            <tr>
                                <th>Pato</th>
                                <th>Descripcion</th>
                            </tr>
                            <tr>
                                <td>ID</td>
                                <td>{planta.id}</td>
                            </tr>
                            <tr>
                                <td>Nombre</td>
                                <td>{planta.nombre}</td>
                            </tr>
                            <tr>
                                <td>Provincia</td>
                                <td>{planta.provincia}</td>
                            </tr>
                            <tr>
                                <td>Canton</td>
                                <td>{planta.canton}</td>
                            </tr>
                            <tr>
                                <td>Distrito</td>
                                <td>{planta.distrito}</td>
                            </tr>
                            <tr>
                                <td>Direccion exacta</td>
                                <td>{planta.direccion_exacta}</td>
                            </tr>
                            <tr>
                                <td>Telefono</td>
                                <td>{planta.telefono}</td>
                            </tr>                   
                        </thead>
                    ))
                }
                </table>
            </div>
        </div>
    </div>
  );
  }
  
  export default CrearP;
