import axios from 'axios';
import React, { useEffect, useState } from 'react';
import Button from "react-bootstrap/Button";
import "./CreateDesign.css";
import Container from "react-bootstrap/Container";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import Image from "react-bootstrap/Image";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  NavLink,
  Link
} from "react-router-dom";

function CrearD() {

  const [idTipoV, setIdTipoV] = useState([]);
  const [trasmision, setTrasmision] = useState([]);
  const [materialAsientos, setMaterialAsientos] = useState([]);
  const [motor, setMotor] = useState([]);
  const [vidriosElectricos, setVidriosElectricos] = useState([]);
  const [espejosElectricos, setEspejosElectricos] = useState([]);
  const [sensoresProxTraseros, setSensoresProxTraseros] = useState([]);
  const [sensoresProxDelanteros, setSensoresProxDelanteros] = useState([]);
  const [nombreDesign, setNombreDesign] = useState([]);
  const [nombreDesigner, setNombreDesigner] = useState([]);

  const handleSubmit = (event) => {
      console.log("Holaaaaa")
      event.preventDefault()
  }
 
  useEffect(() => {
    (async () => {
        const res = await axios.post(`http://localhost:8090/api/InsertarDesign`, { 
            idTipoV: idTipoV,
            trasmision: trasmision,
            material_asientos: materialAsientos,
            motor: motor,
            vidrios_electricos: vidriosElectricos,
            espejos_electricos: espejosElectricos,
            sensorProx_traseros: sensoresProxTraseros,
            sensorProx_delanteros: sensoresProxDelanteros,
            nombredesing: nombreDesign,
            idDesigner: nombreDesigner
        });

        /*const res3 = await axios.get(`http://localhost:8090/api/GetIDPlanta`, { 
            nombre: nombre
        });*/

        /*if (res.data[0]!==undefined){
            if(res.data[0][0]!==undefined){
                setOrderID(res.data[0][0].Num_Factura);
                setSalesBody(res2.data);
                setSales(res.data[0]);
            }else{
                setSales([]);
                setSalesBody([]);
            }
        }*/
        
    })()
},[idTipoV,trasmision,materialAsientos,motor,vidriosElectricos,espejosElectricos,sensoresProxTraseros,sensoresProxDelanteros,nombreDesign,nombreDesigner])


  return (
    <div className="Corporativo">
        <div className="marco">
            <Link to={`/CrearPlanta`}>
                <button>Crear Planta</button>
            </Link>
            <Link to={`/ModificarPlanta`}>
                <button>Modificar Planta</button>
            </Link>
            <Link to={`/EliminarPlanta`}>
                <button>Eliminar Planta</button>
            </Link>
        </div>
        <div className="marco2">
          <form onSubmit={handleSubmit}>
              <label>
                  <p>Nombre</p>
                  <input 
                      type="text"
                      //value={nombre}
                      //onChange={e => setNombre(e.target.value)}
                  />
              </label>
              <label >
                  <p>Provincia</p>
                  <input 
                      type="text"
                      //value={provincia}
                      //onChange={e => setProvincia(e.target.value)}
                  />
              </label>
              <label>
                  <p>Cantón</p>
                  <input 
                      type="text"
                      //value={canton}
                      //onChange={e => setCanton(e.target.value)}
                  />
              </label>
              <label >
                  <p>Distrito</p>
                  <input 
                      type="text"
                      //value={distrito}
                      //onChange={e => setDistrito(e.target.value)}
                  />
              </label>
              <label>
                  <p>Dirección Exacta</p>
                  <input 
                      type="text"
                      //value={direccionExacta}
                      //onChange={e => setDireccionExacta(e.target.value)}
                  />
              </label>
              <label >
                  <p>Teléfono</p>
                  <input 
                      type="text"
                      //value={telefono}
                      //onChange={e => setTelefono(e.target.value)}
                  />
              </label>
          </form>
          <button onclick="myFunction()">Click me</button>
      </div>
    </div>
  );
  }
  
  export default CrearD;
