import React from "react";
import "./CreatePlanta.css";
import Container from "react-bootstrap/Container";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import Image from "react-bootstrap/Image";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  NavLink,
  Link
} from "react-router-dom";

function CrearP() {
  return (
    <div className="Corporativo">
        <div className="marco">
            <Link to={`/CrearPlanta`}>
                <button>Crear Planta</button>
            </Link>
            <Link to={`/ModificarPlanta`}>
                <button>Modificar Planta</button>
            </Link>
            <Link to={`/EliminarPlanta`}>
                <button>Eliminar Planta</button>
            </Link>
        </div>
        <div className="contenedor">
            <div className="marco3">
                <p>
                Sucursal
                </p>
                <select onChange={setearSucursal}>
                {
                    sucursales.map((sucursal) => {
                    return (
                        <option value={`${sucursal.idSucursal}`}>{`${sucursal.nombreSucursal} - ${sucursal.idSucursal}`}</option>
                    )
                    })
                }
                </select>
                <label>
                    <p>Producto</p>
                    <input 
                        type="text"
                        value={nombreProducto}
                        onChange={e => setNombreProducto(e.target.value)}
                    />
                </label>
                <p>
                Categoría
                </p>
                <select onChange={setearCategoria}>
                <option value={""}>Todo</option>
                {
                    categorias.map((categoria) => {
                    return (
                        <option value={`${categoria.nombreCategoria}`}>{`${categoria.nombreCategoria}`}</option>
                    )
                    })
                }
                </select>
            </div>
            <div className="marco2">
                <div className="marco4">
                    <table id="productos"> 
                        <thead >
                            <tr>
                                <th>Producto</th>
                                <th>Categoria</th>
                                <th>Sucursal</th>
                                <th>Cantidad</th>
                            </tr>
                        </thead>
                        { 
                        productos.map(producto => (
                            <tbody>                       
                                <tr>
                                    <td>{producto.Nombre_Producto}</td>
                                    <td>{producto.Categoria}</td>
                                    <td>{producto.Sucursal}</td>
                                    <td>{producto.Cantidad}</td>
                                </tr>
                            </tbody>  
                            ))
                        } 
                    </table>
                </div>
            </div>
        </div>
    </div>
  );
  }
  
  export default CrearP;
