import axios from 'axios';
import React, { useEffect, useState } from 'react';
import Button from "react-bootstrap/Button";
import "./CreatePlanta.css";
import Container from "react-bootstrap/Container";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import Image from "react-bootstrap/Image";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  NavLink,
  Link
} from "react-router-dom";

function CrearP() {

  const [plantas, setPlantas] = useState([]);
    useEffect(() => {
        (async () => {
            const res = await axios.get(`http://localhost:8090/api/GetPlantas`, {
            });
            setPlantas(res.data);
        })()
    },[])

  return (
    <div className="Corporativo">
        <div className="marco">
            <Link to={`/CrearPlanta`}>
                <button>Crear Planta</button>
            </Link>
            <Link to={`/ModificarPlanta`}>
                <button>Modificar Planta</button>
            </Link>
            <Link to={`/EliminarPlanta`}>
                <button>Eliminar Planta</button>
            </Link>
        </div>
        <div className="Tabla">
          <div className="div-c">
              <div className="div-nuevo2">
                  <table id="customers">
                  { 
                      plantas.map(planta => (
                          <thead>
                              <tr>
                                  <th>Planta</th>
                                  <th>Nombre</th>
                                  <th>Provincia</th>
                                  <th>Canton</th>
                                  <th>Distrito</th>
                                  <th>Direccion exacta</th>
                                  <th>Telefono</th>
                              </tr>
                              <tr>
                                  <td>{planta.id}</td>
                                  <td>{planta.nombre}</td>
                                  <td>{planta.provincia}</td>
                                  <td>{planta.canton}</td>
                                  <td>{planta.distrito}</td>
                                  <td>{planta.direccion_exacta}</td>
                                  <td>{planta.telefono}</td>
                              </tr>     
                          </thead>
                      ))
                  }
                  </table>
              </div>
          </div>
        </div>
    </div>
  );
  }
  
  export default CrearP;
