import axios from 'axios';
import React, { useEffect, useState } from 'react';
import Button from "react-bootstrap/Button";
import "./CreatePlanta.css";
import Container from "react-bootstrap/Container";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import Image from "react-bootstrap/Image";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  NavLink,
  Link
} from "react-router-dom";

function ModificarP() {

  const [nombresPlantas, setNombresPlantas] = useState([]);
  const [nombresSeleccionado, setNombreS] = useState('');
  const [id, setID] = useState(1);
  const [nombre, setNombre] = useState('');
  const [provincia, setProvincia] = useState('');
  const [canton, setCanton] = useState('');
  const [distrito, setDistrito] = useState('');
  const [direccionExacta, setDireccionExacta] = useState('');
  const [telefono, setTelefono] = useState('');
  const [infoPlanta, setInfoPlanta] = useState([]);

 
    useEffect(() => {
        (async () => {
            const listaNombres = await axios.get(`http://localhost:8090/api/GetNombresPlanta`, {
            });
            setNombresPlantas(listaNombres.data);
        })()
    },[])

    const setNombreSeleccionado = async (e) => {
        setNombreS(e);
        obtenerID();    
        const res4 = await axios.get(`http://localhost:8090/api/GetPlanta/${id}`);
        setInfoPlanta(res4.data);
    }

    const obtenerID = async () => {
        const res3 = await axios.get(`http://localhost:8090/api/GetIDPlanta/${nombresSeleccionado}`);
        setID(res3);
    }

    const handleSubmit = async (event) => {
        event.preventDefault();

        if(!nombre || !provincia || !canton || !distrito || !direccionExacta || !telefono) return;    

        const res = await axios.post(`http://localhost:8090/api/ModificarPlanta`, { 
            nombre: nombre,
            provincia: provincia,
            canton: canton,
            distrito: distrito,
            direccion_exacta: direccionExacta,
            telefono: telefono
        });

        const res3 = await axios.get(`http://localhost:8090/api/GetIDPlanta/${nombre}`);

        for(let i = 1; i < 4; i++){
            axios.post(`http://localhost:8090/api/CrearBloque`, { 
                idTipoVehiculo: i,
                idPlanta: res3.data[0].id,
                espacioDisponible: 10
            });
        }
    }

  return (
    <div className="Corporativo">
        <div className="marco">
            <Link to={`/CrearPlanta`}>
                <button>Crear Planta</button>
            </Link>
            <Link to={`/ModificarPlanta`}>
                <button>Modificar Planta</button>
            </Link>
            <Link to={`/EliminarPlanta`}>
                <button>Eliminar Planta</button>
            </Link>
        </div>
    
        <div className="marco2">
        <div>
            <label>
                <p>Planta a Modificar</p>
                <select onChange = {e => setNombreSeleccionado(e.target.value)}>
                    { 
                        nombresPlantas.map(name => (
                            <option>{name.nombre}</option>
                        ))
                    }
                </select>
            </label>
        </div>
          <form onSubmit={handleSubmit}>
                    { 
                        infoPlanta.map(info => (
                            <label>
                                <p>Nombre</p>
                                <input 
                                    type="text"
                                    value={info.nombre}
                                />
                            </label>,
                            <label >
                                <p>Provincia</p>
                                <input 
                                    type="text"
                                    value={info.provincia}
                                />
                            </label>,
                            <label>
                                <p>Cantón</p>
                                <input 
                                    type="text"
                                    value={info.canton}
                                />
                            </label>,
                            <label >
                                <p>Distrito</p>
                                <input 
                                    type="text"
                                    value={info.distrito}
                                />
                            </label>,
                            <label>
                                <p>Dirección Exacta</p>
                                <input 
                                    type="text"
                                    value={info.direccionExacta}
                                />
                            </label>,
                            <label >
                                <p>Teléfono</p>
                                <input 
                                    type="text"
                                    value={info.telefono}
                                />
                            </label>
                        ))
                    }
              <div><input type="submit" value="Agregar" /></div>
          </form>
      </div>
    </div>
  );
  }
  
  export default ModificarP;
