import axios from 'axios';
import React, { useEffect, useState } from 'react';
import Button from "react-bootstrap/Button";
import "./CreateDesign.css";
import Container from "react-bootstrap/Container";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import Image from "react-bootstrap/Image";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  NavLink,
  Link
} from "react-router-dom";

function CrearD() {

  const [idTipoV, setIdTipoV] = useState([]);
  const [trasmision, setTrasmision] = useState([]);
  const [materialAsientos, setMaterialAsientos] = useState([]);
  const [motor, setMotor] = useState([]);
  const [vidriosElectricos, setVidriosElectricos] = useState([]);
  const [espejosElectricos, setEspejosElectricos] = useState([]);
  const [sensoresProxTraseros, setSensoresProxTraseros] = useState([]);
  const [sensoresProxDelanteros, setSensoresProxDelanteros] = useState([]);
  const [nombreDesign, setNombreDesign] = useState([]);
  const [nombreDesigner, setNombreDesigner] = useState([]);

  const handleSubmit = async (event) => {
    console.log(event);
    event.preventDefault();
    if(!nombre || !provincia || !canton || !distrito || !direccionExacta || !telefono) return;
    
    const res = await axios.post(`http://localhost:8090/api/InsertarDesign`, { 
        idTipoV: idTipoV,
        trasmision: trasmision,
        material_asientos: materialAsientos,
        motor: motor,
        vidrios_electricos: vidriosElectricos,
        espejos_electricos: espejosElectricos,
        sensorProx_traseros: sensoresProxTraseros,
        sensorProx_delanteros: sensoresProxDelanteros,
        nombredesing: nombreDesign,
        idDesigner: nombreDesigner
    });

    const res3 = await axios.get(`http://localhost:8090/api/GetIDPlanta/${nombre}`);

    for(let i = 1; i < 4; i++){
        axios.post(`http://localhost:8090/api/CrearBloque`, { 
            idTipoVehiculo: i,
            idPlanta: res3.data[0].id,
            espacioDisponible: 10
        });
    }
}

  return (
    <div className="Corporativo">
        <div className="marco">
            <Link to={`/CrearDesign`}>
                <button>Crear Diseño</button>
            </Link>
            <Link to={`/ModificarDiseño`}>
                <button>Modificar Diseño</button>
            </Link>
            <Link to={`/EliminarDiseño`}>
                <button>Eliminar Diseño</button>
            </Link>
        </div>
        <div className="marco2">
        <div>
            <label>
                <p>Diseñador</p>
                <select onInputCapture = {e => setNombreSeleccionado(e.target.value)}>
                    <option ></option>
                    { 

                    }
                </select>
            </label>
            <label>
                <p>Tipo Vehiculo</p>
                <select onInputCapture = {e => setNombreSeleccionado(e.target.value)}>
                    <option ></option>
                    { 

                    }
                </select>
            </label>
        </div>
          <form onSubmit={handleSubmit}>
              <label>
                  <p>Tipo Vehiculo</p>
                  <input 
                      type="text"
                      //value={nombre}
                      //onChange={e => setNombre(e.target.value)}
                  />
              </label>
              <label >
                  <p>Provincia</p>
                  <input 
                      type="text"
                      //value={provincia}
                      //onChange={e => setProvincia(e.target.value)}
                  />
              </label>
              <label>
                  <p>Cantón</p>
                  <input 
                      type="text"
                      //value={canton}
                      //onChange={e => setCanton(e.target.value)}
                  />
              </label>
              <label >
                  <p>Distrito</p>
                  <input 
                      type="text"
                      //value={distrito}
                      //onChange={e => setDistrito(e.target.value)}
                  />
              </label>
              <label>
                  <p>Dirección Exacta</p>
                  <input 
                      type="text"
                      //value={direccionExacta}
                      //onChange={e => setDireccionExacta(e.target.value)}
                  />
              </label>
              <label >
                  <p>Teléfono</p>
                  <input 
                      type="text"
                      //value={telefono}
                      //onChange={e => setTelefono(e.target.value)}
                  />
              </label>
              <label >
                  <p>Teléfono</p>
                  <input 
                      type="text"
                      //value={telefono}
                      //onChange={e => setTelefono(e.target.value)}
                  />
              </label>
              <label >
                  <p>Teléfono</p>
                  <input 
                      type="text"
                      //value={telefono}
                      //onChange={e => setTelefono(e.target.value)}
                  />
              </label>
              <label >
                  <p>Teléfono</p>
                  <input 
                      type="text"
                      //value={telefono}
                      //onChange={e => setTelefono(e.target.value)}
                  />
              </label>
              <label >
                  <p>Teléfono</p>
                  <input 
                      type="text"
                      //value={telefono}
                      //onChange={e => setTelefono(e.target.value)}
                  />
              </label>
          </form>
          <button onclick="myFunction()">Click me</button>
      </div>
    </div>
  );
  }
  
  export default CrearD;
