import "./CreateDesign.css";
import axios from 'axios';
import React, { useEffect, useState,} from 'react';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    NavLink,
    Link
  } from "react-router-dom";

function CreateDesign() {
    const [diseños, setDiseños] = useState([]);
    useEffect(() => {
        (async () => {
            const res = await axios.get(`http://localhost:8090/api/GetVDesigns`, {
            });
            setDiseños(res.data);
        })()
    },[])

    return (
    <div className="Corporativo">
        <div className="marco">
            <Link to={`/CrearDiseño`}>
                <button>Crear Diseño</button>
            </Link>
            <Link to={`/ModificarDiseño`}>
                <button>Modificar Diseño</button>
            </Link>
            <Link to={`/EliminarDiseño`}>
                <button>Eliminar Diseño</button>
            </Link>
        </div>
        <div className="Tabla">
          <div className="div-c">
              <div className="div-nuevo2">
                  <table id="customers">
                      <thead>
                        const [idTipo, setIdTipo] = useState([]);
                        const [trasmision, setTrasmision] = useState([]);
                        const [materialAsientos, setMaterialAsientos] = useState([]);
                        const [motor, setMotor] = useState([]);
                        const [vidriosElectricos, setVidriosElectricos] = useState([]);
                        const [espejosElectricos, setEspejosElectricos] = useState([]);
                        const [sensoresProxTraseros, setSensoresProxTraseros] = useState([]);
                        const [sensoresProxDelanteros, setSensoresProxDelanteros] = useState([]);
                        const [nombreDesign, setNombreDesign] = useState([]);
                        const [nombreDesigner, setNombreDesigner] = useState([]);
                        <tr>
                            <th>Nombre Diseño</th>
                            <th>Nombre Diseñador</th>
                            <th>Tipo Vehiculo</th>
                            <th>Trasmision</th>
                            <th>Material Asientos</th>
                            <th>Vidrios Electricos</th>
                            <th>Espejos Electricos</th>
                            <th>Sensores de Proximidad Traseros</th>
                            <th>Sensores de Proximidad Delanteros</th>
                        </tr>
                      </thead>
                  { 
                      plantas.map(planta => (
                          <tbody>
                              <tr>
                                  <td>{planta.id}</td>
                                  <td>{planta.nombre}</td>
                                  <td>{planta.provincia}</td>
                                  <td>{planta.canton}</td>
                                  <td>{planta.distrito}</td>
                                  <td>{planta.direccion_exacta}</td>
                                  <td>{planta.telefono}</td>
                              </tr>     
                          </tbody>
                      ))
                  }
                  </table>
              </div>
          </div>
        </div>
    </div>
    );
  }
  
  export default CreateDesign;
